import { useEffect, useState } from 'react'
import SkillList from '../skillList/SkillList'
import './skill.scss'
import {
  featuredActivity,
  webActivity,
  mobileActivity,
  designActivity,
  contentActivity,
} from '../../data'

export default function Skill() {
  const [selected, setSelected] = useState('development')
  const [data, setData] = useState([])
  const list = [
    {
      id: 'development',
      title: 'Development',
    },
    {
      id: 'testing',
      title: 'Software Testing',
    },
    {
      id: 'communication',
      title: 'Communication',
    },
    {
      id: 'teamwork',
      title: 'Teamwork',
    },
    {
      id: 'content',
      title: 'Content',
    },
  ]

  useEffect(() => {
    switch (selected) {
      case 'development':
        setData(featuredActivity)
        break
      case 'testing':
        setData(webActivity)
        break
      case 'communication':
        setData(mobileActivity)
        break
      case 'teamwork':
        setData(designActivity)
        break
      case 'content':
        setData(contentActivity)
        break
      default:
        setData(featuredActivity)
    }
  }, [selected])

  return (
    <div className='skill' id='skill'>
      <h1>Skills & Technicals</h1>
      <ul>
        {list.map((item) => (
          <SkillList
            title={item.title}
            active={selected === item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className='container'>
        {data.map((d) => (
          <div className='item'>
            <img src={d.img} alt='' />
            <h3>{d.title}</h3>
          </div>
        ))}
      </div>
    </div>
  )
}
